<script>
    function openModal() {
        const form = document.getElementById('form');
        form.checkValidity();
        const email = document.querySelector('#email');
        if (!email.value) {
            alert('Enter your email address');
            return email.focus();

        }
        document.querySelector('#email-modal').value = email.value;
        $('#myModal').modal('show');
    }
